
public class ControlFlow3 {
//if else if문 예제
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// if/else if/else문
		int i = 15;
		if (i > 5) {
			System.out.println("5보다 큽니다.");
		} else if (i == 5) {
			System.out.println("5와 같습니다.");
		} else {
			System.out.println("5와 작습니다.");
		}
	}
}
